import org.apache.nifi.processor.io.StreamCallback
import java.nio.charset.StandardCharsets
import groovy.json.JsonOutput
import java.util.regex.Pattern

flowFile = session.get()
if(!flowFile) return

flowFile = session.write(flowFile, {inputStream, outputStream ->
    def text = inputStream.getText(StandardCharsets.UTF_8.name())

    def sections = [:]

    // Definition
    def defRegex = Pattern.compile("Python Programming Language.*?(?=Key Features)", Pattern.DOTALL)
    def defMatch = defRegex.matcher(text)
    if (defMatch.find()) {
        sections["definition"] = defMatch.group().trim()
    }

    // Key Features
    def featRegex = Pattern.compile("Key Features of Python:(.*?)(?=Common Uses)", Pattern.DOTALL)
    def featMatch = featRegex.matcher(text)
    if (featMatch.find()) {
        sections["key_features"] = featMatch.group(1).trim()
    }

    // Common Uses
    def usesRegex = Pattern.compile("Common Uses of Python:(.*)", Pattern.DOTALL)
    def usesMatch = usesRegex.matcher(text)
    if (usesMatch.find()) {
        sections["common_uses"] = usesMatch.group(1).trim()
    }

    // Convert to JSON array
    def outputJson = []
    sections.each { k, v ->
        outputJson << [section: k, content: v]
    }

    def jsonStr = JsonOutput.prettyPrint(JsonOutput.toJson(outputJson))
    outputStream.write(jsonStr.getBytes(StandardCharsets.UTF_8))
} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)
