import org.apache.nifi.processor.io.StreamCallback
import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

def flowFile = session.get()
if (!flowFile) return

flowFile = session.write(flowFile, { inputStream, outputStream ->
    def text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    def json = new JsonSlurper().parseText(text)
    def cleaned = [:]

    json.each { key, value ->
        if (value instanceof String) {
            // Trim, then collapse multiple internal spaces to one
            def collapsed = value.trim().replaceAll(/\s+/, ' ')
            cleaned[key] = collapsed.isEmpty() ? null : collapsed
        } else {
            cleaned[key] = value
        }
    }

    def outputJson = JsonOutput.toJson(cleaned)
    outputStream.write(outputJson.getBytes(StandardCharsets.UTF_8))
} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)

